package Basic; import java.util.Scanner; public class Basic{ static void checknum(int num) { if(num%2==0) System.out.println("THE GIVEN NUMBER IS EVEN"); else System.out.println("THE GIVEN NUMBER IS ODD"); } public static void main(String[] args) { int num; Scanner input = new Scanner(System.in); System.out.println("ENTER THE NUMBER"); num = input.nextInt(); Basic.checknum(num); } }
